package com.gzq.demo.leetcode.滑动窗口.至少有K个重复字符的最长子串;

import java.util.HashMap;
import java.util.Map;

public class Solution {
    /**
     * 暴力破解
     * 运行时间超长
     *
     * @param s
     * @param k
     * @return
     */
    public static int longestSubstring(String s, int k) {
        long start = System.currentTimeMillis(); // 记录开始时间
        int count = 0;
        int len = s.length();
        if (len < k) {
            return count;
        }
        for (int i = len; i > 0; i--) {
            for (int j = 0; j < i; j++) {
                String sub = s.substring(j, i);
                count = Math.max(count, longestSubstring2(sub, k));
            }
        }
        long end = System.currentTimeMillis(); // 记录结束时间
        long duration = end - start; // 计算运行时间差值（单位为毫秒）
        System.out.println("程序运行时间1:" + duration + "ms");
        long second = duration / 1000; // 计算运行时间差值（单位为秒）
        System.out.println("程序运行时间1:" + second + "s");
        if (second > 0) {
            long minute = second / 60; // 计算运行时间差值（单位为分钟）
            System.out.println("程序运行时间1:" + minute + "m");
        }
        return count;
    }

    /**
     * 滑动窗口 进行统计当前s中字母的个数
     *
     * @param s
     * @param k
     * @return
     */
    public static int longestSubstring2(String s, int k) {
        int count = 0;
        int len = s.length();
        HashMap<Character, Integer> map = new HashMap<>();
        for (int i = 0; i < len; i++) {
            Character character = s.charAt(i);
            map.put(character, map.getOrDefault(character, 0) + 1);
        }
        for (Map.Entry<Character, Integer> entry : map.entrySet()) {
            if (entry.getValue() < k) {
                count = 0;
                break;
            } else {
                count = len;
            }
        }
        return count;
    }


    public static void main(String[] args) {
        //运行了12分钟
        String s = "";
        int k = 301;
        System.out.println(longestSubstring(s, k));
    }

}
