package com.gzq.demo.leetcode.滑动窗口.至少有K个重复字符的最长子串;

public class Solution2 {
    /**
     * 通过上面代码优化而来
     * 滑动窗口 进行统计当前s中字母的个数
     *
     * @param s
     * @param k
     * @return
     */
    public static int longestSubstring(String s, int k) {
        long start = System.currentTimeMillis(); // 记录开始时间
        int count = 0;
        int len = s.length();
        if (len < k) {
            return count;
        }

        for (int i = len; i > 0; i--) {
            for (int j = 0; j <= len - i; j++) {
                String sub = s.substring(j, j + i);

                // 统计字符出现次数
                int[] charCount = new int[26];
                for (char ch : sub.toCharArray()) {
                    charCount[ch - 'a']++;
                }

                // 判断子串是否满足条件
                boolean valid = true;
                for (int count1 : charCount) {
                    if (count1 > 0 && count1 < k) {
                        valid = false;
                        break;
                    }
                }

                // 更新 count
                if (valid) {
                    count = Math.max(count, sub.length());
                }
            }
        }

        long end = System.currentTimeMillis(); // 记录结束时间
        long duration = end - start; // 计算运行时间差值（单位为毫秒）
        System.out.println("程序运行时间2:" + duration + "ms");
        long second = duration/1000; // 计算运行时间差值（单位为秒）
        System.out.println("程序运行时间2:" + second + "s");
        if (second > 0) {
            long minute = second / 60; // 计算运行时间差值（单位为分钟）
            System.out.println("程序运行时间2:" + minute + "m");
        }
        return count;
    }

    public static void main(String[] args) {
        String s = "";
        int k = 301;
        System.out.println(longestSubstring(s, k));
    }
}
