package com.gzq.demo.leetcode.滑动窗口.至少有K个重复字符的最长子串;

public class Solution3 {

    public static int longestSubstring(String s, int k) {
        return longestSubstringHelper(s, 0, s.length(), k);
    }

    private static int longestSubstringHelper(String s, int start, int end, int k) {
        if (end - start < k) {
            return 0;
        }

        int[] charCount = new int[26];
        for (int i = start; i < end; i++) {
            charCount[s.charAt(i) - 'a']++;
        }

        for (int i = start; i < end; i++) {
            if (charCount[s.charAt(i) - 'a'] < k) {
                return Math.max(
                        longestSubstringHelper(s, start, i, k),
                        longestSubstringHelper(s, i + 1, end, k)
                );
            }
        }

        return end - start;
    }

    public static void main(String[] args) {
        String s = "";
        int k = 301;
        long start = System.currentTimeMillis(); // 记录开始时间
        int result =longestSubstring(s, k);
        long end = System.currentTimeMillis(); // 记录结束时间
        long duration = end - start; // 计算运行时间差值（单位为毫秒）
        System.out.println("程序运行时间3：" + duration + "ms");
        long second = duration/1000; // 计算运行时间差值（单位为秒）
        System.out.println("程序运行时间3:" + second + "s");
        if (second > 0) {
            long minute = second / 60; // 计算运行时间差值（单位为分钟）
            System.out.println("程序运行时间3:" + minute + "m");
        }
        System.out.println(result);  // 输出 3
    }
}
